class Solution {
        private String LCA(HashMap<String, String> map, String r1, String r2) {
            String p1 = r1;
            String p2 = r2;
            while(!p1.equals(p2))
            {
                p1 = map.containsKey(p1) ? map.get(p1) : r2;
                p2 = map.containsKey(p2) ? map.get(p2) : r1;
            }
            return p1;
        }
        public String findSmallestRegion(List<List<String>> regions, String region1, String region2) {
            HashMap<String, String> map = new HashMap<>();
            for(List<String> r : regions)
            {
                for(int i = 1; i < r.size(); i ++)
                    map.put(r.get(i), r.get(0));
            }
            return LCA(map, region1, region2);
        }
    }
