int numSpecial(int** mat, int matSize, int* matColSize){
    int count=0,c1=0;
    for(int i=0;i<matSize;i++)
    {
        for(int j=0;j<*matColSize;j++)
        {
            if(mat[i][j]==1)
            {
                c1=0;
                for(int k=0;k<matSize;k++)
                {
                    if(mat[k][j]==1 && k!=i)
                        c1++;
                }
                if(c1==0)
                {
                    for(int l=0;l<*matColSize;l++)
                    {
                        if(mat[i][l]==1 && l!=j)
                            c1++;
                    }
                }
                if(c1==0)
                    count++;
            }
        }
    }
    return count;
}
