class Solution {
public:
    bool zalgo(string &a, string &b){
        string t = a + "$" + b;
        vector<int> z(t.size(), 0);
        int l = 0, r = 0;
        for(int i = 1; i < t.size(); i++){
            if(i > r){
                r = l = i;
                while(r < t.size() && t[r] == t[r - l])r++;
                z[i] = r - l;
                r--;
            }else{
                int ind = i - l;
                if(i + z[ind] <= r){
                    z[i] = z[ind];
                }else{
                    l = i;
                    while(r < t.size() && t[r] == t[r - l])r++;
                    z[i] = r - l;
                    r--;
                }
            }
            if(z[i] == a.length())return true;
        }
        return false;
    }
    vector<string> stringMatching(vector<string>& words) {
        set<string> ans;
        for(int i = 0; i < words.size(); i++){
            for(int j = 0; j < words.size(); j++){
                if(i == j)continue;
                if(zalgo(words[i], words[j])){
                    ans.insert(words[i]);
                }
            }
        }
        vector<string> st(ans.begin(), ans.end());
        return st;
    }
};
