class Solution {
    int[] m = new int[27];
    int[] mark = new int[27];
    public boolean canConvert(String str1, String str2) {
        int n = str1.length();
        for (int i = 0; i < n; i += 1) {
            int t1 = str1.charAt(i) - 'a' + 1, t2 = str2.charAt(i) - 'a' + 1;
            if (m[t1] != 0 && m[t1] != t2) return false;
            m[t1] = t2;
        }
        for (int i = 1; i <= 26; i += 1) {
            if (m[i] == 0) return true;
        }
        boolean hasCircle = false;
        for (int i = 1; i <= 26; i += 1) {
            if (m[i] == i) continue;
            if (mark[i] == 0) {
                mark[i] = 1;
                int p = m[i];
                while (mark[p] == 0) {
                    mark[p] = 1;
                    p = m[p];
                }
                if (p != i) return true; 
                else hasCircle = true;
            }
        }
        return !hasCircle;
    }
}
