class Solution {
public:
    vector<vector<int> >grid;
    int ans;
    void flip(int r,int c,int k){
        for(int i=0;i<k;i++){
            for(int j=0;j<k;j++){
                grid[r+i][c+j]=!grid[r+i][c+j];
            }
        }
    }
    void solve(int m,int n,int squares){
        if(squares>=ans) return;
        int flag=0;
        for(int i=0;i<m;i++){
            for(int j=0;j<n;j++){
                if(grid[i][j]==0){
                    flag=1;
                    int max_size=min(m-i,n-j);
                    for(int r=i;r<m;r++){
                        for(int c=j;c<n;c++){
                            if(grid[r][c]==1) max_size=min(max_size,max(abs(r-i),abs(c-j)));
                        }
                    }
                    for(int k=max_size;k>=1;k--){
                        flip(i,j,k);
                        solve(m,n,squares+1);
                        flip(i,j,k);
                    }
                    return;
                }
            }
        }
        if(flag==0) ans=min(ans,squares);
    }
    int tilingRectangle(int m, int n) {
        grid.assign(m,vector<int>(n,0));
        ans=INT_MAX;
        solve(m,n,0);
        return ans;
    }
};
