class Solution {
    public int findSubstringInWraproundString(String p) {
        int[] count = new int[26];

        int i=0;
        while (i<p.length()) {
            int j=i+1;
            while (j < p.length() && ((p.charAt(j) - 'a') % 26 == (p.charAt(j-1) + 1 - 'a') % 26)) {
                j++;
            }

            int len = j-i;

            for (int k=0; k<Math.min(len, 26); k++) {
                count[(p.charAt(i) + k - 'a') % 26] = Math.max(count[(p.charAt(i) + k - 'a') % 26], len - k);
            }

            i = j;
        }

        int result = 0;
        for (int c: count) result+=c;
        return result;
    }
}
