class Solution {
public:
    bool makePalindrome(string s) {

        int left=0;
        int right = s.length()-1;
        int cnt=0;
        while(left<right){
            if(s[left]!=s[right]){
                cnt++;
            }
            if(cnt>2) return false;


            left++;
            right--;
        }
        return true;

    }
};
