class Solution(object):
    def validWordAbbreviation(self, word, abbr):
        #i12iz4n internationalization
        wordIndex = 0
        index = 0
        while index < len(abbr):
            if abbr[index].isdigit():
                if abbr[index] == '0':
                    return False
                number = 0
                while index < len(abbr) and abbr[index].isdigit():
                    number = number * 10 + int(abbr[index])
                    index += 1
                wordIndex += number
            else:
                if wordIndex >= len(word):
                    return False
                if abbr[index] != word[wordIndex]:
                    return False
                else:
                    index += 1
                    wordIndex += 1

        return wordIndex == len(word)

        """
        :type word: str
        :type abbr: str
        :rtype: bool
        """
