class Solution {
public:
    bool isValidSerialization(string preorder) {    
        int unclosed_nodes = 0;
        for(int i=0; i<preorder.size(); i++) {
            if(preorder[i] == '#') {
                unclosed_nodes--;
                if(unclosed_nodes < 0 && i!=preorder.size()-1) {
                    return false;
                }
                i++;
            }
            else {
                unclosed_nodes++;
                i++;
                while(i < preorder.size() && preorder[i] != ',') {
                    i++;
                }
            }

        }
        if(unclosed_nodes != -1) {
            return false;
        }
        return true;
    }
};
